(57, 'Serta Tuhan', '<p style=\"text-align: center;\">VERSE</p>\r\n<p style=\"text-align: center;\">1</p>\r\n<p style=\"text-align: center;\">Nikmat benar &lsquo;pabila alam bangun</p>\r\n<p style=\"text-align: center;\">Burung berkicau fajar merekah</p>\r\n<p style=\"text-align: center;\">Jauh lebih nikmat bila kusadari</p>\r\n<p style=\"text-align: center;\">Sampai sekarang Kau temankulah</p>\r\n<p style=\"text-align: center;\">2</p>\r\n<p style=\"text-align: center;\">Dengan Engkau di pagi hari baru</p>\r\n<p style=\"text-align: center;\">Dalam belaian bayu nan sejuk</p>\r\n<p style=\"text-align: center;\">Dengan Engkau &lsquo;ku jadi terpesona</p>\r\n<p style=\"text-align: center;\">&lsquo;Ku menyembah lututku kutekuk.</p>\r\n<p style=\"text-align: center;\">3</p>\r\n<p style=\"text-align: center;\">Dengan Engkau setiap pagi baru</p>\r\n<p style=\"text-align: center;\">Kurasa tambah indah karya-Mu</p>\r\n<p style=\"text-align: center;\">Dan demikian &lsquo;ku bertambah sadar</p>\r\n<p style=\"text-align: center;\">Tambah dekat kurasa kasih-Mu</p>\r\n<p style=\"text-align: center;\">4</p>\r\n<p style=\"text-align: center;\">Dan bila k&rsquo;lak pagi berakhir tiba</p>\r\n<p style=\"text-align: center;\">Bila jiwaku bangun dari g&rsquo;lap</p>\r\n<p style=\"text-align: center;\">Akan segar keyakinanku bangun</p>\r\n<p style=\"text-align: center;\">Kaulah Tuhanku kawanku tetap</p>\r\n<p style=\"text-align: center;\">&nbsp;</p>', 'NKI 12', 'https://www.youtube.com/watch?v=NzPun1AFxh4', '2021-01-17 16:37:12', '2021-01-17 16:37:12'),
(58, 'SLAMANYA KAU TUHANKU', '<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Dalam AnugrahMu</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Dalam NaunganMu</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Ku dapat merasakan</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">kemuliaanMu Tuhan</span><br style=\"margin: 0px; padding: 0px;\" /><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\"><br style=\"margin: 0px; padding: 0px;\" /></span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Kau yang menetapkan</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Kau yang mencurahkan</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Kuasa dari tahtaMu</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Membangkitkan rohku</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\">&nbsp;</div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><strong style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">REFF:</span></strong></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">S\'lamanya Kau Tuhanku</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Yang terutama di hidupku</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Tak akan kulepaskan</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Pengharapan di dalamMu</span></div>', 'Pujian', 'https://www.youtube.com/watch?v=qX3bXIk1D7w', '2021-01-17 16:37:59', '2021-01-17 16:37:59'),
(59, 'SIAPAKAH AKU INI TUHAN', '<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Kasih setia-Mu yang kurasakan</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Lebih tinggi dari langit biru</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Kebaikan-Mu yang t\'lah kau nyatakan</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Lebih dalam dari lautan</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\">&nbsp;</div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Berkat-Mu yang telah kuterima</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Sempat membuatku terpesona</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Apa yang tak pernah kupikirkan</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Itu yang Kau sediakan bagiku</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\">&nbsp;</div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><strong style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">REFF:</span></strong></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\">&nbsp;</div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Siapakah aku ini Tuhan</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Jadi biji mata-Mu</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">Dengan apakah ku balas Tuhan</span></div>\r\n<div style=\"margin: 0in; padding: 0px; color: #060606; font-family: Raleway, Arial, sans-serif; font-size: 16px; background-color: #ffffff; line-height: 18.4px; text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">S\'lain puji dan sembah Kau</span></div>\r\n<p style=\"text-align: center;\"><span style=\"margin: 0px; padding: 0px; color: black; font-family: cambria, serif;\">&nbsp;</span></p>', 'Pujian', 'https://www.youtube.com/watch?v=bG0olkYEFhY', '2021-01-17 16:39:17', '2021-01-17 16:39:17'),
(60, 'Serta Tuhan', '<p style=\"text-align: center;\">VERSE</p>\r\n<p style=\"text-align: center;\">1</p>\r\n<p style=\"text-align: center;\">Serta Tuhan ketika terbit fajar</p>\r\n<p style=\"text-align: center;\">Dan bayang malam s&rsquo;kalian habislah</p>\r\n<p style=\"text-align: center;\">Serta Tuhan suara-Mu aku dengar</p>\r\n<p style=\"text-align: center;\">serta Tuhan itulah terindah</p>\r\n<p style=\"text-align: center;\">2</p>\r\n<p style=\"text-align: center;\">Serta Tuhan ketika siang sampai</p>\r\n<p style=\"text-align: center;\">Lebih dekat ku hampir pada-Mu</p>\r\n<p style=\"text-align: center;\">Serta Tuhan tiada aku kuatir</p>\r\n<p style=\"text-align: center;\">Serta Tuhan itulah surgaku</p>\r\n<p style=\"text-align: center;\">3</p>\r\n<p style=\"text-align: center;\">Serta Tuhan ketika malam datang</p>\r\n<p style=\"text-align: center;\">Inilah doa yang kusembahkan</p>\r\n<p style=\"text-align: center;\">Hendaklah Bapa lindungkan anak-Nya</p>\r\n<p style=\"text-align: center;\">Dan pagi b&rsquo;ri hadirat-Mu Tuhan</p>\r\n<p style=\"text-align: center;\">4</p>\r\n<p style=\"text-align: center;\">Serta Tuhan ketika pagi akhir</p>\r\n<p style=\"text-align: center;\">Bila saat terbangkit jiwaku</p>\r\n<p style=\"text-align: center;\">yang amat indah pada masa itu</p>\r\n<p style=\"text-align: center;\">Ya, inilah ku serta Tuhanku.</p>', 'NKI 12A', 'https://www.youtube.com/watch?v=NzPun1AFxh4', '2021-01-17 16:39:54', '2021-01-17 16:39:54'),
(61, 'Yesus Yang Maha Mulia', '<p style=\"text-align: center;\">VERSE</p>\r\n<p style=\"text-align: center;\">1</p>\r\n<p style=\"text-align: center;\">Tuhanku Yesus</p>\r\n<p style=\"text-align: center;\">raja alam raya</p>\r\n<p style=\"text-align: center;\">Allah juga manusia</p>\r\n<p style=\"text-align: center;\">Kau kukasihi kau junjunganku</p>\r\n<p style=\"text-align: center;\">Bahagiaku yang baka.</p>\r\n<p style=\"text-align: center;\">2</p>\r\n<p style=\"text-align: center;\">Indah tamasya</p>\r\n<p style=\"text-align: center;\">Indah sawah ladang</p>\r\n<p style=\"text-align: center;\">Sungguh eloknya berseri</p>\r\n<p style=\"text-align: center;\">Jauh lebih indah, kau Tuhan Yesus</p>\r\n<p style=\"text-align: center;\">Engkau menghibur yang sedih.</p>\r\n<p style=\"text-align: center;\">3</p>\r\n<p style=\"text-align: center;\">Indah t&rsquo;rang surya</p>\r\n<p style=\"text-align: center;\">Indah sinar bulan</p>\r\n<p style=\"text-align: center;\">Alam bintang yang t&rsquo;rus megah</p>\r\n<p style=\"text-align: center;\">Jauh lebih indah Yesus terang-Mu</p>\r\n<p style=\"text-align: center;\">Di surga dan di dunia.</p>', 'NKI 13', 'https://www.youtube.com/watch?v=Etb-cI5lxMQ', '2021-01-17 16:41:54', '2021-01-17 16:41:54');
